[spoiler]package com.rs.game.player.content.commands;

import com.rs.Settings;
import com.rs.game.Animation;
import com.rs.game.ForceTalk;
import com.rs.game.Hit;
import com.rs.game.Hit.HitLook;
import com.rs.game.World;
import com.rs.game.WorldObject;
import com.rs.game.WorldTile;
import com.rs.game.item.Item;
import com.rs.game.minigames.FightPits;
import com.rs.game.npc.NPC;
import com.rs.game.player.LoyaltyManager;
import com.rs.game.player.Player;
import com.rs.game.player.Skills;
import com.guardian.ItemManager;
import com.rs.game.player.dialogues.impl.AccountManager;
import com.rs.utils.DisplayNames;
import com.rs.utils.Encrypt;
import com.rs.utils.IPBanL;
import com.rs.utils.NPCSpawns;
import com.rs.utils.SerializableFilesManager;
import com.rs.utils.ShopsHandler;
import com.rs.utils.Utils;
import com.rs.utils.spawning.ObjectSpawning;

public class Owner {

	public static boolean processCommand(Player player, String[] cmd, boolean console, boolean clientCommand) {
		String name;
		Player target;
		boolean loggedIn = true;

		if (clientCommand) {
			if (cmd[0].equals("tele")) {
				cmd = cmd[1].split(",");
				int plane = Integer.valueOf(cmd[0]);
				int x = Integer.valueOf(cmd[1]) << 6 | Integer.valueOf(cmd[3]);
				int y = Integer.valueOf(cmd[2]) << 6 | Integer.valueOf(cmd[4]);
				player.setNextWorldTile(new WorldTile(x, y, plane));
				return true;
			}
			return true;
		}

		if (cmd[0].equals("hint")) {
			int id = Integer.parseInt(cmd[1]);
			player.getHintIconsManager().addHintIcon(player, id, -1, false);
			return true;
		}

		if (cmd[0].equals("rhint")) {
			player.getHintIconsManager().removeUnsavedHintIcon();
			return true;
		}

		if (cmd[0].equals("shop")) {
			int id = Integer.parseInt(cmd[1]);
			ShopsHandler.openShop(player, id);
			return true;
		}

		if (cmd[0].equals("test")) {
			player.getControlerManager().startControler("LobbyArea");
			return true;
		}

		if (cmd[0].equals("fix")) {
			name = "";
			for (int i = 1; i < cmd.length; i++)
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");
			target = World.findPlayer(name);
			if (target == null)
				return true;
			target.setNeedsFixed(true);
			SerializableFilesManager.savePlayer(target);
			player.getPackets().sendGameMessage("Done.");
			return true;
		}

		if (cmd[0].equals("ipban")) {
			name = "";

			for (int i = 1; i < cmd.length; i++)
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");

			target = World.findPlayer(name);

			if (target.isOwner()) {
				return true;
			}

			loggedIn = World.isOnline(name);

			IPBanL.ban(target, loggedIn);

			player.getPackets().sendGameMessage("You've permanently ipbanned " + (loggedIn ? target.getDisplayName() : name) + ".");
			return true;
		}

		if (cmd[0].equals("unipban")) {
			name = "";

			for (int i = 1; i < cmd.length; i++)
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");

			target = World.findPlayer(name);

			if (target.isOwner()) {
				return true;
			}

			IPBanL.unban(target);

			player.getPackets().sendGameMessage("You've un-banned " + (loggedIn ? target.getDisplayName() : name) + ".");
			return true;
		}

		if (cmd[0].equals("resetpvp")) {
			name = "";

			for (int i = 1; i < cmd.length; i++)
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");

			target = World.findPlayer(name);

			if (target.isOwner()) {
				return true;
			}

			target.setKillCount(0);
			target.setDeathCount(0);
			target.setPvpPoints(0);

			player.sendMessage("PvP reset for "+target.getDisplayName()+"");

			if (World.isOnline(name))
				target.sendMessage("All of your pvp points, kills, and deaths have been reset.");
			SerializableFilesManager.savePlayer(target);
			return true;
		}

		if (cmd[0].equals("setkills")) {
			name = "";
			for (int i = 1; i < cmd.length; i++)
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");
			target = World.findPlayer(name);
			if (target.isOwner()) {
				return true;
			}
			int amount = Integer.parseInt(cmd[1]);
			target.setKillCount(amount);
			player.sendMessage(""+target.getDisplayName()+"'s kills have been set to "+amount+"");
			if (World.isOnline(name))
				target.sendMessage("Your kill count have been set to "+amount+"");
			SerializableFilesManager.savePlayer(target);
			return true;
		}

		if (cmd[0].equals("setdeaths")) {
			name = "";
			for (int i = 1; i < cmd.length; i++)
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");
			target = World.findPlayer(name);
			if (target.isOwner()) {
				return true;
			}
			int amount = Integer.parseInt(cmd[1]);
			target.setDeathCount(amount);
			player.sendMessage(""+target.getDisplayName()+"'s deaths have been set to "+amount+"");
			if (World.isOnline(name))
				target.sendMessage("Your death count have been set to "+amount+"");
			SerializableFilesManager.savePlayer(target);
			return true;
		}

		if (cmd[0].equals("getitems")) {
			int[] toAdd = { 1039, 1041, 1043, 1045, 1047, 1049, 1051, 1037 };
			player.getInventory().addItems(toAdd, Integer.MAX_VALUE);
			return true;
		}

		if (cmd[0].equals("npca")) {
			NPC npc = World.getNpc(Integer.parseInt(cmd[1]));
			npc.setNextAnimation(new Animation(Integer.parseInt(cmd[2])));
			return true;
		}

		if (cmd[0].equals("shop")) {
			int id = Integer.parseInt(cmd[1]);
			ShopsHandler.openShop(player, id);
			return true;
		}

		if (cmd[0].equals("setdp")) {
			name = "";
			int value = Integer.parseInt(cmd[1]);
			for (int i = 2; i < cmd.length; i++) {
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");
			}
			target = World.findPlayer(name);

			if (target == null)
				return true;

			target.setDungPoints(value);

			player.sendMessage(""+target.getDisplayName()+"'s dung points are now "+target.getDungPoints()+".");
			if (World.isOnline(name)) {
				target.sendMessage("Your Dung Points have been set to "+value+" by "+player.getDisplayName()+".");
			}
			SerializableFilesManager.savePlayer(target);
			return true;
		}

		if (cmd[0].equals("getdp")) {
			name = "";
			for (int i = 1; i < cmd.length; i++) {
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");
			}
			target = World.findPlayer(name);

			if (target == null)
				return true;

			player.sendMessage(""+target.getDisplayName()+" has "+Utils.formatNumber(target.getDungPoints())+" dung points. ");
			return true;
		}

		if (cmd[0].equals("setdiff")) {
			name = "";
			int difficulty = Integer.parseInt(cmd[1]);
			for (int i = 2; i < cmd.length; i++) {
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");
			}
			target = World.findPlayer(name);

			if (target == null)
				return true;

			target.setDifficulty(difficulty);

			player.sendMessage(""+target.getDisplayName()+"'s difficulty is now "+AccountManager.diffs[difficulty - 1]+".");
			if (World.isOnline(name)) {
				target.sendMessage("Your difficulty has been forced to "+AccountManager.diffs[difficulty - 1]+" by "+player.getDisplayName()+".");
			}
			SerializableFilesManager.savePlayer(target);
			return true;
		}

		if (cmd[0].equals("allow")) {
			name = "";
			for (int i = 1; i < cmd.length; i++) {
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");
			}
			target = World.findPlayer(name);

			if (target == null) {
				return true;
			}

			target.setAllowChange(true);
			player.sendMessage(""+target.getDisplayName()+" can now change their difficulty.");

			if (World.isOnline(name)) {
				target.sendMessage("You are now allowed to change your difficulty setting.");
			}
			SerializableFilesManager.savePlayer(target);
			return true;
		}


		if (cmd[0].equals("remove")) {
			if (player.examinedObject == null) {
				player.sendMessage("Last examined object is null.");
				return true;
			}
			World.removeObject(player.examinedObject, true);
			player.getPackets().sendGameMessage("Removed object " + player.examinedObject.getId() + ".");
			player.examinedObject = null;
			return true;
		}


		if (cmd[0].equals("getid")) {
			String name = "";
			for (int i = 1; i < cmd.length; i++) {
				name += cmd[i] + ((i == cmd.length - 1) ? "" : " ");
			}
			ItemSearch.searchForItem(player, name);
			return true;
		}

		if (cmd[0].equals("god")) {
			player.setHitpoints(Short.MAX_VALUE);
			player.getEquipment().setEquipmentHpIncrease(Short.MAX_VALUE - 990);
			for (int i = 0; i < 10; i++) {
				player.getCombatDefinitions().getBonuses()[i] = 5000;
			}
			for (int i = 14; i < player.getCombatDefinitions().getBonuses().length; i++) {
				player.getCombatDefinitions().getBonuses()[i] = 5000;
			}
			return true;
		}

		if (cmd[0].equals("tonpc")) {
			if (cmd.length < 2) {
				player.getPackets().sendPanelBoxMessage("Use: ::tonpc id(-1 for player)");
				return true;
			}
			try {
				player.getAppearence().transformIntoNPC(Integer.valueOf(cmd[1]));
			} catch (NumberFormatException e) {
				player.getPackets().sendPanelBoxMessage("Use: ::tonpc id(-1 for player)");
			}
			return true;
		}

		if (cmd[0].equals("switchemotes")) {
			player.setUsingAltEmotes(!player.isUsingAltEmotes());
			player.sendMessage("Alternate emotes are now "+(player.isUsingAltEmotes() ? "enabled" : "disabled")+"");
			return true;
		}

		if (cmd[0].equals("title")) {
			try {
				if (cmd.length < 2) {
					player.getPackets().sendGameMessage("Use: ::title id");
					return true;
				}
				TitleHandler.set(player, Integer.parseInt(cmd[1]));
			} catch (NumberFormatException e) {
				player.getPackets().sendGameMessage("Use: ::title id");
			}
			return true;
		}

		if (cmd[0].equals("bank")) {
			if (player.isAtWorldBoss()) {
				player.sendMessage("You are not allowed to bank from here.");
				return true;
			}
			player.stopAll();
			player.getBank().openBank();
			return true;
		}

		if (cmd[0].equals("spec")) {
			player.getCombatDefinitions().resetSpecialAttack();
			return true;
		}

		if (cmd[0].equals("setdisplay")) {
			player.sendMessage("Click on the noticeboard at home if you wish to change your display name.");
			return true;
		}

		if (cmd[0].equals("getcmds")) {
			player.getPackets().sendPanelBoxMessage("::cut id# - begins a cutscene");
			player.getPackets().sendPanelBoxMessage("::inter id# - opens an interface");
			player.getPackets().sendPanelBoxMessage("::sound id# - plays a sound");
			player.getPackets().sendPanelBoxMessage("::music id# = plays a song");
			player.getPackets().sendPanelBoxMessage("::open id# - opens an interface with line numbers");
			player.getPackets().sendPanelBoxMessage("::gfx id# - starts a graphic");
			player.getPackets().sendPanelBoxMessage("::anim id# - starts a player animation");
			player.getPackets().sendPanelBoxMessage("::master - maxes your stats");
			player.getPackets().sendPanelBoxMessage("::tonpc id# - transforms you into an npc");
			player.getPackets().sendPanelBoxMessage("::item id# amount - spawns an item (amount is optional)");
			player.getPackets().sendPanelBoxMessage("::switchemote - switches certain skilling emotes");
			player.getPackets().sendPanelBoxMessage("::title id# - changes your title");
			player.getPackets().sendPanelBoxMessage("::bank - anywhere banking");
			player.getPackets().sendPanelBoxMessage("::spec - maxes out your spec bar");
			player.getPackets().sendPanelBoxMessage("::setdisplay - set a display name");
			player.getPackets().sendPanelBoxMessage("::resetdisplay - resets your display name.");
			return true;
		}

		return false;
	}


}
