[code]    public static String[][] CommandsOrganizer;
        static{
            CommandsOrganizer=new String[][] {
                //{"::example", "playerRightsNeeded", "EventsID"},
                {"switch","0","12855"},
                {"yell","0","7311"},
                {"mypos","0","12345"},
                {"mypk","0","12346"},
                {"mystats","0","12347"},
