program Autologin;
const
username = ''; //USERNAME
password = ''; //PASSWORD

procedure PanicExit;
begin
if(IsFKeyDown(2)) then
terminatescript;
end;

procedure BFmouse(mousex,mousey:integer);
var  randx, randy:integer;
begin
MoveMouseSmoothEx(randx,randy,30,30,30,17,5);
end;

procedure Login;
begin
activateclient;
PanicExit;
BFmouse(458, 292);
PanicExit;
sleep(500);
PanicExit;
clickmouse(458, 292, true);
PanicExit;
sleep(500);
PanicExit;
BFmouse(372, 255);
PanicExit;
sleep(500);
PanicExit;
clickmouse(372, 255, true);
PanicExit;
sleep(500);
sendkeys(username);
PanicExit;
sendkeys(chr(13));
PanicExit;
sendkeys(password);
PanicExit;
BFmouse(301, 323);
PanicExit;
sleep(500);
PanicExit;
clickmouse(301, 323, true);
end;

begin
Login;
end.[/code]
