if(findbitmap(enter, x, y))then
movemousesmooth(x,y); clickmouse(x,y,true);
wait(1000) sendkeyssilent(User+chr(13)) wait(1000) sendkeyssilent(Pass)
wait(1000)
if(findbitmap(loginbutton, x, y))then
movemousesmooth(x,y); clickmouse(x,y,true); wait(3000)
repeat Wait(1000) until(FindColor(x,y,colour,0,0,548,362)) wait(200) movemousesmooth(x,y) wait(2000) clickmouse(x,y,true) end;
procedure clicktargets; begin i :=0
Target := DTMFromString('78DA63D4606460D8CC80021E5C7004D38C503' + 'EA31CA69A99B6E6A86ACC30D5380676A2A801009F6C06B8');
wait(1000) If(not(FindDTM(target,x,y,0,0,548,362)))Then writeln('Cant find the target, trying to teleport to targets!') wait(2000) cleardebug; Writeln('teleporting...') sendkeys('::range'+chr(13))
repeat FindDTM(Target,x,y,0,0,999,999) movemousesmooth(x,y) wait(500) clickmouse(x,y,true); i :=i+1; wait(900) cleardebug; Writeln('Have clicked ' + Inttostr(i) + ' times') until(i=clicks); cleardebug; Writeln('Successfully finished training your ranged!'); end;
begin activateclient; wait(500); if(alreadyloggedin=0)then begin login; end; wait(1000); clicktargets; end.[/scar]
