channel.description:
This is basically an API class I made for making stat lookups for rs easy,

[code]
<?php
	class StatGrabber {

		private $name = null;
		private $file = null;
		private $stats = array();
		private $hiscores = array("Overall",
			"stats" => array(	
				"Attack", "Defence", "Strength",
				"Hitpoints", "Ranged", "Prayer",
				"Magic", "Cooking", "Woodcutting",
				"Fletching", "Fishing", "Firemaking",
				"Crafting", "Smithing",	"Mining",
				"Herblore", "Agility", "Thieving",
				"Slayer", "Farming", "Runecrafting",
				"Hunter", "Construction", "Summoning"
			),
			"minigames" => array(
				"Duel Tournaments", "Bounty Hunters",
				"Bounty Hunter Rogues", "Fist of Guthix",
				"Mobilising Armies"
			)
		);
		public $full = null;
		public function __construct($name) {
			if(is_null($name)) {
				die("Username can't be null!");
			}
			$this->name = strtolower($name);
			$this->file = fopen("http://hiscore.runescape.com/index_lite.ws?player={$this->name}", "rb");
			$this->lines = array();
		}

		public function build_list() {
			if(is_null($this->name))
				die("Username is not set");
			$contents = "";
			$lines = array();
			while(!feof($this->file)) {
				$lines[] = fgets($this->file);
			}
			array_pop($lines);
			for($i=0;$i<25;$i++) {
				$split = explode(",", $lines[$i]);
				$a = array(
					"rank" => $split[0],
					"level" => $split[1],
					"xp" => trim($split[2])
				);
				$this->final[$this->hiscores["stats"][$i]] = $a;
				array_push($this->stats, $a);
			}
			for($i=25,$d=0;$i<count($lines);$i++,$d++) {
				$split = explode(",", $lines[$i]);
				$a = array(
					"rank" => $split[0],
					"score" => trim($split[1])
				);
				$this->final[$this->hiscores["minigames"][$i]] = $a;
				array_push($this->stats, $a);
			}
			array_pop($this->final);
		}

		public function get_attack_group() {
			return $this->stats[1];
		}

		public function get_defence_group() {
			return $this->stats[2];
		}

