Add this Emotes class in com.rs2.model.content.

[code]
package server.world.entity.player.content;

import server.world.entity.Animation;
import server.world.entity.Gfx;
import server.world.entity.player.Player;
import server.world.item.Item;


/**
 * Complete emotes for the emote tab.
 * 
 * @author Artem Batutin(artembatutin@gmail.com)
 * 
 */
public class Emotes {

	private enum Emote {
		YES(168, 885),
		NO(169, 856),
		BOW(164, 858),
		ANGRY(167, 859),
		THINK(161, 857),
		WAVE(163, 863),
		SHRUG(52058, 2113),
		SHEER(171, 862),
		BECKON(165, 864),
		LAUGH(170, 861),
		JUMP_FOR_JOY(52054, 2109),
		YAWN(52056, 2111),
		DANCE(166, 866),
		JIG(52051, 2106),
		TWIRL(52052, 2107),
		HEADHANG(52053, 2108),
		CRY(161, 860),
		BLOW_KISS(43092, 0x558),
		PANIC(52050, 2105),
		RASPBERRY(52055, 2110),
		CALP(172, 865),
		SALUTE(52057, 2112),
		GOBLIN_BOW(52071, 0x84F),
		GOBLIN_SALUTE(52072, 0x850),
		GLASS_BOX(2155, 1131),
		CLIMB_ROPE(25103, 1130),
		LEAN(25106, 1129),
		GLASSWALL(2154, 1128),
		ZOMBIE_WALK(72032, 3544),
		ZOMBIE_DANCE(72033, 3543),
		SCARED(59062, 2836),
		BUNNY_HOP(72254, 6111);

		private int buttonId;
	    private Animation ANIMATION;

	    Emote(int buttonId, int animationId) {
	    	this.buttonId = buttonId;
	    	this.ANIMATION = new Animation(animationId);
		}

		public int getButtonId() {
			return this.buttonId;
		}
	}

	private enum AccomplishmentEmote {
		//NAME 1ID	2ID  3ID ANIM GFX
		ATTACK(9747, 9748, 10639, 4959, 823),
		STRENGTH(9750, 9751, 10640, 4981, 828),
		DEFENCE(9753, 9754, 10641, 4961, 843),
		RANGING(9756, 9757, 10642, 4973, 832),
		PRAYER(9759, 9760, 10643, 4979, 829),
		MAGIC(9762, 9763, 10644, 4939, 813),
		RUNECRAFTING(9765, 9766, 10645, 4947, 817),
		HITPOINTS(9768, 9769, 10647, 4971, 833),
		AGILITY(9771, 9772, 10648, 4977, 830),
		HERBLORE(9774, 9775, 10649, 4969, 835),
		THIEVING(9777, 9778, 10650, 4965, 826),
		CRAFTING(9780, 9781, 10651, 4949, 818),
		FLETCHING(9783, 9784, 10652, 4937, 812),
		SLAYER(9786, 9787, 10653, 4967, 827),
		CONSTRUCTION(9789, 9790, 10654, 4953, 820),
		MINING(9792, 9793, 10655, 4941, 814),
		SMITHING(9795, 9796, 10656, 4943, 815),
		FISHING(9798, 9799, 10657, 4951, 819),
		COOKING(9801, 9802, 10658, 4955, 821),
		FIREMAKING(9804, 9805, 10659, 4975, 831),
		WOODCUTTING(9807, 9808, 10660, 4957, 822),
		FARMING(9810, 9811, 10661, 4963, 825),
		HUNTER(9948, 9949, 10646, 5158, 907);

		private Item firstItem;
		private Item secondItem;
		private Item thirdItem;
		private Animation ANIMATION;
		private Gfx GRAPHIC;

		AccomplishmentEmote(int firstItem, int secondItem, int thirdItem, int animationId, int graphicId) {
			this.firstItem = new Item(firstItem, 1);
			this.secondItem = new Item(secondItem, 1);
			this.thirdItem = new Item(thirdItem, 1);
			this.ANIMATION = new Animation(animationId);
			this.GRAPHIC = new Gfx(graphicId, 0);
		}

		public boolean hasItem(Player player) {
			if(player.getEquipment().getItemContainer().contains(firstItem.getId()) ||
				player.getEquipment().getItemContainer().contains(secondItem.getId()) ||
				player.getEquipment().getItemContainer().contains(thirdItem.getId())) {
				return true;
			}
			return false;
		}

	}

	public static boolean castEmote(final Player player, int buttonId) {
		for (Emote emote : Emote.values()) {
			  if(emote.getButtonId() == buttonId) {
				  player.animation(emote.ANIMATION);
				  return false;
			  } else if(buttonId == 74108) {
				  for (AccomplishmentEmote accomplishment : AccomplishmentEmote.values()) {
					  if(accomplishment.hasItem(player)) {
						  player.animation(accomplishment.ANIMATION);
						  player.gfx(accomplishment.GRAPHIC);
						  break;
					  } else
						  player.getServerPacketBuilder().sendMessage("You must be wearing a skillcape to perform this emote.");
				  }
			  }
		}
		return false;
	}
}
