program KoensAutoTalker4n00bsV1;

const
  Word1 = 'Selling cooked trout 31gp each. -=YourName=-';  //This is what you will type
procedure SendString(text : string);

var
  TempInt, TempInt1 : Integer;
  TempChar : Char;

begin
  ActivateClient;

  for TempInt := 1 to Length(text) do
  begin
    TempChar := StrGet(text, TempInt);
    TempInt1 := GetKeyCode(TempChar);
    KeyDown(TempInt1);
    Wait(30 + Random(35));
    KeyUp(TempInt1);
    Wait(45 + Random(35));
  end;
  SendKeysVB('{ENTER}', True);
  Wait(1200+random(2100));
end;

begin
  repeat
  sendstring(word1)
  until(false)          //Infinite Loop (if You dont want then delete + delete "repeat"
end.
