channel.description:
this bone collector works...but after a full inventory it just keeps trying to pickup the bones...and wont bury them could someone help?

heres script:[scar]Procedure BoneCollector;
begin
   Wait(200+random(100));
   Status('Collecting bones...');
   x:=262;
   y:=174;
   If(FindColorSpiral(x,y,(BoneColor),5,5,515,337))then
   begin
      Mouse(x,y,5,5,false);
      wait(50+random(25));
      If(FindBitmapIn(TakeBones,x,y,5,5,515,337))then
      begin
         Mouse(x,y,0,0,true);
         Wait(15+random(5));
         BonesCollected:=BonesCollected+1;
         LastBuried:=LastBuried+1;
         MapFlag;
         wait(500+random(100));
      end;
      If(FindBitmapIn(TakeBonesY,x,y,5,5,515,337))then
      begin
         Mouse(x,y,0,0,true);
         Wait(15+random(5));
         BonesCollected:=BonesCollected+1;
         LastBuried:=LastBuried+1;
         MapFlag;
         wait(500+random(100));
      end;
   end;
end;
[/scar]

heres bone burier part...[scar]Procedure BoneBurier;
begin
OpenBag;
Wait(200+random(100));
Status('Burying bones...');
   If(FindColorSpiral(x,y,(BoneInvColor),555,216,740,465))then
   repeat
    LastBuried:=0;
    Mouse(x,y,10,10,true);
    Wait(1500+random(300));
    BonesBuried:=BonesBuried+1;
   until(not(FindColorSpiral(x,y,(BoneInvColor),555,216,740,465)));
end;[/scar]

