import org.jdesktop.jdic.browser.WebBrowser;

import javax.swing.*;
import java.awt.*;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.awt.event.KeyEvent;
import java.awt.event.KeyListener;
import java.net.MalformedURLException;
import java.net.URL;

/**
 * @author moparisthebest
 */
public class ClientExample implements ActionListener, KeyListener {

    public static WebBrowser webBrowser = new WebBrowser();
    private JFrame frame;
    private String frameTitle = "ClientExample";
    public int lowmem = 1;
    public int plugin = 0;

    public static void main(String[] args) {
        new ClientExample(args);
    }

    /**
     * Creates a new instance of ClientExample
     */
    public ClientExample(String[] args) {
        initUI();
        //webBrowser.setDebug(true);
        //startURL("http://www.moparisthebest.com/rs2.php?world=10&lowmem=1&plugin=0");
        startURL("http://www.google.com/");
    }

    private void startURL(String url) {
        try {
            webBrowser.setURL(new URL(url));
        } catch (MalformedURLException e) {
            System.out.println(e.getMessage());
            return;
        }
    }

    private void initUI() {
        try {
            JFrame.setDefaultLookAndFeelDecorated(true);
            JPopupMenu.setDefaultLightWeightPopupEnabled(false);

            frame = new JFrame(frameTitle);
            frame.setLayout(new BorderLayout());

            frame.setResizable(false);
            frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);

            webBrowser.setPreferredSize(new Dimension(765, 513));

            JMenuBar menuBar = new JMenuBar();


            String[] menuButtons = new String[]{
                    "Type", "Click"
            };

            for (String name : menuButtons) {
                JButton menuItem = new JButton(name);
                menuItem.addActionListener(this);
                menuBar.add(menuItem);
            }

            frame.getContentPane().add(menuBar, BorderLayout.NORTH);
            frame.getContentPane().add(webBrowser, BorderLayout.CENTER);
            frame.pack();
            frame.setVisible(true);
        } catch (Exception e) {
            System.out.println("A general exception occured in initUI()");
            e.printStackTrace();
        }
    }


    public void actionPerformed(ActionEvent evt) {
        String cmd = evt.getActionCommand();
        if (cmd != null) {
            if (cmd.equals("Click")) {

                //XXX do mouseevent sending to webBrowser here

            } else if (cmd.equals("Type")) {

                //XXX do keyevent sending to webBrowser here

/*                     if(webBrowser instanceof KeyListener){//somthing I already tried, didnt work
                     System.out.println("yay press press");
                     ((KeyListener) webBrowser).keyPressed(new KeyEvent(webBrowser, KeyEvent.KEY_PRESSED , System.currentTimeMillis(), 0, KeyEvent.VK_UNDEFINED, 'a'));
                     ((KeyListener) webBrowser).keyReleased(new KeyEvent(webBrowser, KeyEvent.KEY_RELEASED , System.currentTimeMillis(), 0, KeyEvent.VK_UNDEFINED, 'a'));
                     }
*/              }
        }
    }


    public void keyPressed(KeyEvent keyevent) {
        System.out.println("keysomthing");
    }

    public void keyReleased(KeyEvent keyevent) {
        System.out.println("keysomthing");
    }

    public void keyTyped(KeyEvent keyevent) {
        System.out.println("keysomthing");
    }

}
