procedure lumberjackloop;
begin
repeat
if not LoggedIn then Exit;
ReplyToMsg;
wait(waittime +random(200));
FindObj(x, y,'op d' , objcolor, 10);
Mouse(x, y , 4 , 4, true);
AntiRnd;
until (InvFull);
loads:=loads+1
wait(waittime +random(200));
AntiRnd;
end;
                       {~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~}
                       {~~~~~~~~~~~~~~~~~~~~~~~~~~~~ores loop~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~}
                       {~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~}
procedure minerloop;
begin
repeat
if not LoggedIn then Exit;
