This is my Simba implementation of the [url=http://en.wikipedia.org/wiki/Shellsort]Shellsort[/url] algorithm. It's not much of a script, more so a snippet. Enjoy :)

[code=pascal]program new;

procedure ShellSort(var A: array of LongInt);
var
  Gap, Temp, I, J: Integer;
begin
  Gap := GetArrayLength(A) div 2;
  while (Gap > 0) do begin
    for I := Gap to High(A) do begin
      J := I - Gap;
      while (J >= 0) do begin
        if (A[J] > A[J + Gap]) then begin
          Temp := A[J];
          A[J] := A[J + Gap];
          A[J + Gap] := Temp;
        end else Break;
        DecEx(J, Gap);
      end;
    end;
    Gap := Gap div 2;
  end;
end;

var
  I: Integer;
  A: array of Integer;
begin
  A := [6, 4, 5, 1, 2, 3, 10, 7, 9, 8];
  ShellSort(A);
  for I := 0 to High(A) do
    WriteLn(IntToStr(A[I]));
end.[/code]
