[code]/////////////////////////////////////////////////////
//                  SLITHZ TYPER (V.1)             //
//              Typing Terror Script               //
/////////////////////////////////////////////////////

///////////////////INFORMATION////////////////////////////////
//Start this script on the game menu                        //
//It will run throught the game (with the end boss)         //
//Please report any bugs to Slithz at www.moparisthebest.com//
//////////////////////////////////////////////////////////////
program TTA;
procedure startup;
begin
WriteLn ('    Thank you for using Slithz Typer (V.1)   ')
WriteLn ('                -----=:=-----                ')
WriteLn (' YOU HAVE 5 SECONDS TO SELECT THE GAME SCREEN')
wait(5000)
clickmouse(92,177,true)
wait(1000)
clickmouse(108,68,true)
end;
procedure typingterror;
begin
repeat
sendkeys('q');   //To make the typing slower LOWER the wait times
wait(10);        //To make it faster make the wait times HIGHER
sendkeys('w');
wait(10);        //////EXAMPLES//////
sendkeys('e');   //FAST: wait(5)   //
wait(10);        //MEDIUM: wait(10)//
sendkeys('r');   //SLOW: wait (20) //
wait(10);        ////////////////////
sendkeys('t');
wait(10);
sendkeys('y');
wait(10);
sendkeys('u');
wait(10);
sendkeys('i');
wait(10);
sendkeys('o');
wait(10);
sendkeys('p');
wait(10);
sendkeys('a');
wait(10);
sendkeys('s');
wait(10);
sendkeys('d');
wait(10);
sendkeys('f');
wait(10);
sendkeys('g');
wait(10);
sendkeys('h');
wait(10);
sendkeys('j');
wait(10);
sendkeys('k');
wait(10);
sendkeys('l');
wait(10);
sendkeys('z');
wait(10);
sendkeys('x');
wait(10);
sendkeys('c');
wait(10);
sendkeys('v');
wait(10);
sendkeys('b');
wait(10)
sendkeys('n');
wait(10);
sendkeys('m');
until(getcolor(305,276)=52224)
end;

procedure popups;
begin
clickmouse(305,276,true)
end;

begin
startup
wait(100)
repeat
typingterror
popups
until(getcolor(183,409)=4209722)
end.


[/code]
