[code]procedure SendKeysHuman(Text: String);
var
  a: integer;
begin
  for a := 1 To Length(Text) do
  begin
    SendKeys(PChar(MidStr(Text, a, 1)), True);
    Wait(100 + Random(150));
    Application.ProcessMessages;
  end;
end;[/code]

