[scar]program New;

function Square(int : integer) : integer;
var i : integer;
begin
 for i := 0 to int do
  begin
   if (i*i = int) then
    result := i;
  end;
 if result = 0 then
  writeln('[Error] Invalid number');
end;

function SquareT(a,b : integer) : extended;
var a2,b2,c : extended;
begin
a2 := a * a;
b2 := b * b;
c := a2 + b2;
Result := Sqrt(c);
end;


begin
