{.include SRL/SRL.scar}
{.Include SRL/SRL/Skill/Mining.Scar}



Procedure Setup;
Begin
SetupSRL;
SetupSRLMining;
PerfectNorth;
ClearDebug;
End;

Procedure MineRocks;
Begin
Repeat
 DwarfItem;
 Wait(1);
 FindNormalRandoms;
 Wait(1);
 FindTalk;
 If(FindColor(x, y, 10529968, 121, 213, 884, 711))Then
 Wait(150);
 FindColor(x, y, 5137051, 599, 37, 971, 117);
 Wait(150);
 Mouse(x, y, 3, 4, True);
Wait(2500+random(2500));
If(FindColor(x, y, 4418453, 121, 213, 884, 711))Then
Mouse(x, y, 3, 2, True);
Wait(6500+Random(6500));
Until(InvFull);
DropAll;
End;



begin
Setup;
Repeat
MineRocks;
Until(InvFull);
end.[/scar]
