anyway this script will make a 300 post herd for thumbing  enjoy




DRAG THE CROSSHAIR

[code]
program makeaherd;
{.include SRL/SRL.scar}
var
posts, x, y, click: integer;

const
Your_Colour = 9984315;

procedure aposts;

begin
click := BitmapFromString(15, 13, 'beNqLnGEdOejQfyRAjGKsbOI' +
       'V47ILUzEeuzDdjF8x8b6gUDEeD1ITAQC91xzj');

posts:=1;

repeat
wait(500);
if findbitmap(click, x, y)then
begin
mouse(x, y - 15, 0, 0, True);
end;
wait(500);
sendkeys('a' + IntToStr(posts) );
wait(500);
if findbitmap(click, x, y)then
wait(500);
movemousesmooth(x, y);
wait(500);
clickmouse(x, y, true);
wait(250);
posts:=posts+1;
until( posts = 101 );
end;

procedure bposts;
begin
repeat
wait(500);
if findbitmap(click, x, y)then
begin
mouse(x, y - 15, 0, 0, True);
end;
wait(500);
sendkeys('b' + IntToStr(posts) );
wait(500);
if findbitmap(click, x, y)then
wait(500);
movemousesmooth(x, y);
wait(500);
clickmouse(x, y, true);
wait(250);
posts:=posts+1;
until( posts = 201 );
end;

procedure cposts;
begin
repeat
wait(500);
if findbitmap(click, x, y)then
begin
mouse(x, y - 15, 0, 0, True);
end;
wait(500);
sendkeys('c' + IntToStr(posts) );
wait(500);
if findbitmap(click, x, y)then
wait(500);
movemousesmooth(x, y);
wait(500);
clickmouse(x, y, true);
wait(250);
posts:=posts+1;
until( posts = 301 );
end;

begin
setupsrl;
aposts;
bposts;
cposts;
end.
