procedure PrinceMouse(A,b:integer);
begin
MouseSpeed:=3;
  Mouse(A,b,10,10,true);
end;


Procedure bank;
begin
if(BankScreen)then
  begin
    Wait(Random(1000)+2000);
    FixBank;
    DepositAll;
    Wait(Random(1000)+2000);
    CloseWindow;
    Banks:=Banks+1;
    end;
end;

