  Result:=True;
end;

procedure  WalkToSomeMine;
var _,__:integer;
var Lost:boolean;
begin
    RoadWalk(KRoadColor(RoadColor),'E');
    CountFlag(2);
    Randoms_;
    RoadWalk(KRoadColor(RoadColor),'E');
    CountFlag(2);
    Randoms_;
    RoadWalk(KRoadColor(RoadColor),'SW');
    CountFlag(2);
    begin
    RoadWalk(KRoadColor(RoadColor),'SE');
      CountFlag(2);
      RoadWalk(KRoadColor(RoadColor),'S');
      CountFlag(2);
        begin
        RoadWalk(KRoadColor(RoadColor),'S');
        CountFlag(2);
        RoadWalk(KRoadColor(RoadColor),'S');
         begin
    if FindSymbol(_,__,'mining spot')then
      begin
       PrinceMouse(_,__);
        CountFlag(2);
        Randoms_;
       end else
         Lost:=true;
        Randoms_;
       end;
    end;
  end;
end;



Procedure BankForThrills;
var X,y:integer;
begin
if FindSymbol(X,y,'Mining spot') then
  begin
    PrinceMouse(X,y);
    CountFlag(2);
      end else
      Lost_:=true;
      begin
      RoadWalk(KRoadColor(RoadColor),'NE');
      WriteLn('Stage 1');
      CountFlag(2);
      Randoms_;
      RoadWalk(KRoadColor(RoadColor),'NE');
      WriteLn('Stage 2');
      CountFlag(2);
      Randoms_;
      RoadWalk(KRoadColor(RoadColor),'NE');
      WriteLn('Stage 3');
      CountFlag(2);
      Randoms_;
      RoadWalk(KRoadColor(RoadColor),'NE');
      WriteLn('Stage 4');
      CountFlag(2);
      Randoms_;
      RoadWalk(KRoadColor(RoadColor),'E');
      WriteLn('Stage 5');
      Randoms_;
      RoadWalk(KRoadColor(RoadColor),'E');
      WriteLn('Stage 6');

      CountFlag(2);
        begin
          if FindBank('VEB') then
          CountFlag(2);
            Randoms_
            Bank;
        end;
    end;
end;

procedure MineOreForThrills;
begin
  repeat
  MineOre_;
  Randoms_;
  until(InvFull);
end;


