var
xyx,yxy,xxx,yyy,xb,yb:Integer;
{***************************SETUP**********************************************}
const
     Breaks = True ;     //Take Breaks
     AntiBanOn = True ;  //Use Anti-Ban
     TripsToDo=100 ;     //How Many Trips You Want To Do
     PinN='0000';        //Your Pin #, If you dont have one leave 0000
