Yah, a StickRPG*C* Script i made forever ago. Doesnt work for everyone(i think it has to do with screen resolution...), but if it does, its very helpful.

Enjoy.

[code]
program TrainMe;
var
x,y : integer;

/////////////////////////////
{StickRPG Trainer Made by
{M0ng00se. Thanks to 1337 macro
{and ClashFan for the help.
{Work **ature Doesnt work unless
{You have an Alarm clock.
{If You are having any issues,
{Contact m0ng00se at
{ Supercrosser_23@hotmail.com }

const
//{School Options}
GoToClass=false;
GoToGym=false;
Study=false;
//{Other}
Work=false;
TrainCharm=true;

Procedure LeaveHouse;
begin
MoveMouse(455,265);
ClickMouse(455,265, true);
wait(200);
ClearDebug;
If(GoToClass=true)then
Writeln('Now Attending Class!');
If(GoToGym=True)then
writeln('Now training your Strength!!');
If(Study=True)then
Writeln('Now Studying!!');
If(TrainCharm=true)then
Writeln('Now training your Charm!!')
end;

Procedure Learn;
begin
If(GoToClass=True)then
begin
keydown(VK_RIGHT);
sleep(4300)
KeyDown(VK_DOWN);
sleep(4700);
keyup(VK_RIGHT);
Keyup(VK_DOWN);
KeyDown(vk_right);
sleep(3000);
KeyDown(vk_up);
sleep(3000);
KeyUp(vk_right);
keyup(vk_up);
wait(10);
MoveMouse(205,184);
Wait(10);
Repeat
ClickMouse(205,184, true);
wait(200);
Until(FindColor(x,y,204,321,2,375,37));
wait(1000);
MoveMouse(391,265);
wait(100);
ClickMouse(391,265, true);
wait(300);
end;
end;

Procedure WorkOut;
begin
If(GoToGym=True)then
begin;
keydown(VK_RIGHT);
sleep(4300)
KeyDown(VK_DOWN);
sleep(4700);
keyup(VK_RIGHT);
Keyup(VK_DOWN);
KeyDown(vk_right);
sleep(3000);
KeyDown(vk_up);
sleep(3000);
KeyUp(vk_right);
keyup(vk_up);
wait(10);
MoveMouse(390,140);
Wait(10);
Repeat
ClickMouse(390,140, true);
wait(200);
Until(FindColor(x,y,204,321,2,375,37));
wait(1000);
MoveMouse(391,265);
wait(100);
ClickMouse(391,265, true);
wait(300);
end;
end;

Procedure Studying;
begin
If(Study=True)then
begin
keydown(VK_RIGHT);
sleep(4300)
KeyDown(VK_DOWN);
sleep(4700);
keyup(VK_RIGHT);
Keyup(VK_DOWN);
KeyDown(vk_right);
sleep(3000);
KeyDown(vk_up);
sleep(3000);
KeyUp(vk_right);
keyup(vk_up);
wait(10);
MoveMouse(205,135);
Wait(10);
Repeat
ClickMouse(205,135, true);
wait(200);
Until(FindColor(x,y,204,321,2,375,37));
wait(1000);
MoveMouse(391,265);
wait(100);
ClickMouse(391,265, true);
wait(300);
end;
end;

Procedure BarWalk;
begin
If(TrainCharm=True)then
begin
KeyDown(vk_right);
sleep(3500);
Keydown(vk_down);
sleep(2600);
KeyUp(vk_right);
KeyDown(vk_down);
sleep(2000);
KeyDown(vk_left);
sleep(2500);
keyup(vk_down);
keyup(vk_left);
///Drinking
MoveMouse(230,135);
Repeat
ClickMouse(230,135, true);
wait(200);
Until(FindColor(x,y,204,336,19,354,27));
wait(900);
MoveMouse(452,267);
ClickMouse(452,267,true);
wait(500);
end;
end;

Procedure GoWork;
begin
If(Work=true)then
begin
KeyDown(vk_right);
sleep(6200);
KeyDown(vk_down);
sleep(2500);
keyup(vk_down);
keyup(vk_right);
wait(1000);
MoveMouse(380,270);
Repeat
ClickMouse(380,270, true);
Until(FindColor(x,y,204,321,2,375,37));
wait(1000);
MoveMouse(375,320);
ClickMouse(375,320,true);
wait(500);
end;
end;



Procedure GoHomeSleep;
begin
MoveMouse(480,22);
ClickMouse(480,22,true);
wait(20);
MoveMouse(452,336);
ClickMouse(452,336, true);
wait(200);
MoveMouse(215,186);
wait(20);
ClickMouse(215,186, true);
wait(100);
MoveMouse(485,260);
ClickMouse(485,260, true);
wait(100);
ClickMouse(485,260, true);
end;


Begin
LeaveHouse;
repeat
Learn;
Workout;
Studying;
BarWalk;
GoWork;
GoHomeSleep;

Until(false)
end.
