So guys I got bored today and decided to code the Fibonacci Sequence

[code]

program FibonacciSequence;
var n1, n2, n3, repeatcount:integer;


procedure firstrun;
begin
n1:=0;
n2:=1;
n3:=n1+n2;
writeln(IntToStr(n1));
writeln(IntToStr(n2));
writeln(IntToStr(n3));
end;

procedure mainrun;
begin
n1:=n2
n2:=n3
n3:=n1+n2
writeln(IntToStr(n3));
end;

begin
repeatcount:=0;
firstrun;
repeat
repeatcount:=repeatcount+1
mainrun;
until(repeatcount = 15);
end.

[/code]
