[code]package org.peterbjornx.libjs5.net.js5;

import org.peterbjornx.libjs5.io.JagexDataOutputStream;
import org.peterbjornx.libjs5.io.JagexDataInputStream;
import org.peterbjornx.libjs5.util.Js5Exception;

import java.net.Socket;
import java.io.IOException;
import java.util.Queue;
import java.util.PriorityQueue;
import java.util.HashMap;
import java.nio.ByteBuffer;

/**
 * User: peterbjornx<br>
 * Date: Dec 22, 2009<br>
 * Time: 3:51:56 PM<br>
 *
 * @author Peterbjornx
 */
public class Js5Connection {
    private Socket sock;
    private JagexDataOutputStream out;
    private JagexDataInputStream in;
    private Queue<Js5Request> request_queue;
    private Js5State state;
    private boolean drop_on_error = false;
    private HashMap<Integer,Js5Request> incomplete_files;
    private String server;
    private int port;
    private boolean isLoggedIn;
    private int version;
    private Js5Request currentrequest;
    private long lastTime = 0l;

    public Js5Connection() {
        request_queue    = new PriorityQueue<  Js5Request>();
        incomplete_files = new HashMap<Integer,Js5Request>();
    }

    /**
     * Requests the server to drop the network queues
     */
    public void requestServersideDrop(){
        try {
            out.writeByte(7);
            out.writeInt3(0);
            out.flush();
        } catch (Exception e) {
            handleIOException();
        }
    }

    /**
     * Notifys the server of a status change
     * @param isLoggedIn if the client is logged in.
     */
    public void setLoggedIn(boolean isLoggedIn){
        this.isLoggedIn = isLoggedIn;
        if (state == Js5State.READY)
            notifyLoggedIn();
    }

    /**
     * Notifys the server of a status change
     */
    private void notifyLoggedIn(){
        try {
            out.writeByte(isLoggedIn ? 2 : 3);
            out.writeInt3(0);
            out.flush();
        } catch (IOException e) {
            handleIOException();
        }
    }

    /**
     * Processes the request queues.
     * @throws Js5Exception when the server sends a non-zero response
     */
    @SuppressWarnings({"PointlessBitwiseExpression"})
    public void process() throws Js5Exception {
        if (state == Js5State.ERROR){
            dropconnection();
            reRequestAllFiles();
            incomplete_files.clear();
            if (!drop_on_error)
                state = Js5State.RECONNECTING;
        }
        if (state == Js5State.NOT_CONNECTED)
            return;
        if (state == Js5State.RECONNECTING)
            connect0();
        try{
            if (state == Js5State.AWAIT_RESPONSE){
                if (in.available() == 0)
                    return;
                int response = in.read();
                switch (response){
                    case 6:
                        throw Js5Exception.OUTOFDATE;
                    case 7:
                    case 9:
                        throw Js5Exception.FULL;
                    case 0:
                        state = Js5State.READY;
                        //notifyLoggedIn();
                        break;
                    case -1:
                        throw new IOException("Unable to read js5 response");
                    default:
                        throw Js5Exception.UNSPECIFIED;
                }
            }
            if (state != Js5State.READY)
                return;
            long latency = System.currentTimeMillis() - lastTime;
            if (lastTime != 0 && latency > 10000){
                System.out.println("Dropping - Serverside Timeout");
                dropconnection();
                reRequestAllFiles();
                incomplete_files.clear();
                state = Js5State.RECONNECTING;
                lastTime = System.currentTimeMillis();
                return;
            }
            if (in.available() != 0)
                lastTime = System.currentTimeMillis();
            if (!request_queue.isEmpty() && incomplete_files.size() < 3){
                for (Js5Request r = request_queue.poll(); r != null; r = request_queue.poll()){
                    out.writeByte(/*r.isType1   () ? 1 :*/ 0);
                    out.writeInt3(r.hashCode  ()        );
                    out.flush();
                    incomplete_files.put(r.hashCode(), r);
                }
            }

            for (int c = 0;c < 100;c++){
                int avail = in.available();
                if (avail == 0)
                    break;
                if (avail <  0)
                    throw new IOException("Error polling data");
                int dataNeeded = 0;
                if (currentrequest == null)
                    dataNeeded = 8;
                else if (currentrequest.getBlockPos() == 0)
                    dataNeeded = 1;
                if (dataNeeded > 0){
                    if (in.available() >= dataNeeded){
                        if (currentrequest == null){
                            int storeid    = in.readz() & 0xFF;
                            int fileid     = in.readShort();
                            System.out.println("Got file:"+storeid+" "+fileid);
                            currentrequest = incomplete_files.get((storeid << 16) | fileid);
                            currentrequest.setCompression(in.read() & 0x7f);
                            currentrequest.setSize(in.readInt());
                            byte data[]    = new byte[currentrequest.getSize() + (currentrequest.getCompression() == 0 ? 5 : 9)];
                            data[0]        = (byte) (currentrequest.getCompression());
                            data[1]        = (byte) (currentrequest.getSize() >>> 24);
                            data[2]        = (byte) (currentrequest.getSize() >>> 16);
                            data[3]        = (byte) (currentrequest.getSize() >>>  8);
                            data[4]        = (byte) (currentrequest.getSize() >>>  0);
                            currentrequest.setData(data);
                            currentrequest.setBlockPos(8);
                            currentrequest.setFilePos (5);
                        } else if (currentrequest.getBlockPos() == 0) {
                            if ((in.peek() & 0xff) == 255){
                                currentrequest.setBlockPos(1);
                            }else
                                currentrequest = null;
                        }
                    }
                } else {
                    int dataToRead    = 512 - currentrequest.getBlockPos();
                    int fileRemaining = currentrequest.getData().length - currentrequest.getFilePos();
                    int blockpos      = currentrequest.getBlockPos();
                    if (fileRemaining  < dataToRead)
                        dataToRead = fileRemaining;
                    if (in.available() < dataToRead)
                        dataToRead = in.available();
                    byte[] data = currentrequest.getData();
                    int reallyRed = in.read(data,currentrequest.getFilePos(),dataToRead);
                    currentrequest.setData(data);
                    currentrequest.setBlockPos(blockpos + reallyRed);
                    currentrequest.setFilePos (currentrequest.getFilePos() + reallyRed);
                    if (currentrequest.getFilePos() != currentrequest.getData().length){
                        if (currentrequest.getBlockPos() == 512)
                            currentrequest.setBlockPos(0);
                    } else {
                        currentrequest.setComplete(true);
                        currentrequest = null;
                    }
                }
            }
        } catch(IOException e){
            handleIOException();
        } catch(Js5Exception e){
            dropconnection();
            throw e;
        }
    }

    /**
     * Requests a file from the connected update server
     * @param storeid The store to get the file from
     * @param fileid The number of the file to get
     * @param type1 If this file should be downloaded with type 1
     * @return The request for further reference
     */
    public Js5Request request(int storeid,int fileid,boolean type1){
        Js5Request r = new Js5Request(type1,storeid,fileid);
        if (!request_queue.offer(r))
            return null;
        return r;
    }

    /**
     * Requests a file from the connected update server
     * @param storeid The store to get the file from
     * @param fileid The number of the file to get
     * @return The request for further reference
     */
    public Js5Request request(int storeid,int fileid){
        Js5Request r = new Js5Request(storeid,fileid);
        if (!request_queue.offer(r))
            return null;
        return r;
    }

    /**
     * Drops the connection and destroys the streams
     */
    public void dropconnection() {
        try{
            sock.close();
        } catch (IOException ignored){}
        sock = null;
        out = null;
        in = null;
        currentrequest = null;
        state = Js5State.NOT_CONNECTED;
    }

    /**
     * Connects to the given server.
     * @param server the server to connect to
     * @param port the port to use
     * @param version the current RuneScape version
     */
    public void connect(String server, int port, int version){
        this.server = server;
        this.port   = port;
        this.version= version;
        connect0();
    }

    /**
     * Handles the actual process of connecting.
     */
    private void connect0() {
        try{
            sock = new Socket               (server, port);
            in   = new JagexDataInputStream (sock.getInputStream() );
            out  = new JagexDataOutputStream(sock.getOutputStream());
            out.writeByte(15     );
            out.writeInt (version);
            state = Js5State.AWAIT_RESPONSE;
        } catch (IOException e){
            state = Js5State.ERROR;
        }
    }

    /**
     * Adds all files not fully downloaded to the request queue
     */
    private void reRequestAllFiles() {
        for (Js5Request r : incomplete_files.values())
            request_queue.offer(r);
    }

    /**
     * Common handler for I/O errors
     */
    private void handleIOException() {
        state = Js5State.ERROR;
    }

    public Js5State getState() {
        return state;
    }
}
[/code][code]package org.peterbjornx.libjs5.util.zip;

import org.peterbjornx.libjs5.io.JagexDataInputStream;

import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.DataInputStream;
import java.util.zip.GZIPInputStream;
import java.util.zip.CRC32;

/**
 * Decompresses JS5 containers (.jcf files)
 * @author Peterbjornx
 */
public class Js5Container {
    private byte[] payload    ;
    private int    rawsize    ;
    private int    zipsize    ;
    private int    compression;
    private int    crc32      ;

    /**
     * Decodes a js5 container
     * @param raw The raw data to decode
     * @throws IOException if an error occurs durinc decoding
     */
    public Js5Container(byte[] raw) throws IOException {
        JagexDataInputStream jdis = new JagexDataInputStream(new ByteArrayInputStream(raw));
        compression = jdis.readByte();
        zipsize     = jdis.readInt ();
        if (compression != 0)
            rawsize = jdis.readInt ();
        else
            rawsize = zipsize;
        payload = new byte[zipsize];
        jdis.readFully(payload);
        CRC32 crc = new CRC32();
        crc.update(raw);
        crc32 = (int) crc.getValue();
    }

    /**
     * Gets the decompressed contents of the container
     * @return The decompressed contents of the container
     */
    public byte[] decompress(){
        if (compression == 0)
            return payload;
        byte[] result = new byte[rawsize];
        if (compression != 1){
            try {
                DataInputStream dis = new DataInputStream(new GZIPInputStream(new ByteArrayInputStream(payload)));
                dis.readFully(result);
            } catch (IOException e) {
                System.out.println("Error decompressing GZIP compressed containter");
                return null;
            }
        } else {
            try {
                DataInputStream ins = new DataInputStream(new CBZip2InputStream(new ByteArrayInputStream(payload),0));
                ins.readFully(result);
                ins.close();
            } catch (IOException e) {
                System.out.println("Error decompressing BZIP2 compressed containter");
                return null;
            }
        }
        return result;
    }

    /**
     * Gets the size of the compressed data
     * @return the size of the compressed data
     */
    public int getZipsize() {
        return zipsize;
    }

    /**
     * Sets the size of the compressed data
     * @param zipsize the size of the compressed data
     */
    public void setZipsize(int zipsize) {
        this.zipsize = zipsize;
    }

    /**
     * Gets the compression type<br>
     * 0 - No compression<br>
     * 1 - BZIP2 Compression<BR>
     * 2 - GZIP Compression
     * @return the compression type
     */
    public int getCompression() {
        return compression;
    }

    /**
     * Sets the compression type<br>
     * 0 - No compression<br>
     * 1 - BZIP2 Compression<BR>
     * 2 - GZIP Compression
     * @param compression the compression type
     */
    public void setCompression(int compression) {
        this.compression = compression;
    }

    /**
     * Gets the size of the decompressed data
     * @return The size of the decompressed data
     */
    public int getRawsize() {
        return rawsize;
    }

    /**
     * Sets the size of the decompressed data
     * @param rawsize The size of the decompressed data
     */
    public void setRawsize(int rawsize) {
        this.rawsize = rawsize;
    }

    public int getCRC32() {
       return crc32;
    }
}
