if(command.startsWith("buyearth") && (command.length() > 9)){
int amount = Integer.parseInt(command.substring(9));
int stockcost = 50000; //price per stock
if(playerHasItem(995, amount*stockcost) && amount <= 10000) {
deleteItem(995, amount*stockcost);
addItem(576, amount);
sM("You invest "+amount*stockcost+" in "+amount+" Earth stocks.");
sM("Say ::sellearth (amount) to sell - they do not have to be in noted form");
} else {
sM("You need "+amount*stockcost+"gp to purchase "+amount+" Earth Stocks.");
sM("You can only buy 10000 stocks at a time");
}
}
if(command.startsWith("buyorbs") && (command.length() > 8)){
