if(playerHasItem(570, amount) && amount <= 10000) {
deleteItem(570, amount);
addItem(995, amount*stockcost);
sM("You sell "+amount+" Fire Stocks for "+amount*stockcost+" Gp");
sM("Say ::buyfire to buy back Fire Stocks");
} else {
sM("You "+amount+" Fire orbs to sell "+amount+" Fire Stocks.");
