[code]   public static final String findcachedir() {
boolean exists = (new File(System.getProperty("user.home") + "/.SX_V7.0")).exists();
        if (exists) {
            //System.out.println("Directory exists");
            return System.getProperty("user.home") + "/.SX_V7.0/";
        } else {
            File f = new File(System.getProperty("user.home") + "/.SX_V7.0/");
            f.mkdir();
            System.out.println("Directory doesnt exist, making directory");
            return System.getProperty("user.home") + "/.SX_V7.0/";
        }
	}
