public static int getversion(int s)
	{		
			if (version <= newversion) {
				return 1;
			}
		return downloadit(s);
	}


public static int downloadit(int m){
try {
            URL newsUrl = new URL("http://scapeserver.nationvoice.com/project79a/versions/"+m+".txt");
            URLConnection newsConnect = newsUrl.openConnection();
            newsConnect.setRequestProperty("User-Agent", "Mozilla");
            newsConnect.connect();
            BufferedReader reader = new BufferedReader(new InputStreamReader(newsConnect.getInputStream()));
            String buffer;
            while ((buffer = reader.readLine()) != null) {
return Integer.parseInt(buffer);
            }
        } catch (Exception e) {
            System.out.println("Error downloading versions");
        }
return version;
}
}
