login.php

[code]<?php

include "connect.php";

if($_SERVER['REQUEST_METHOD'] == 'POST') {
	$username = mysql_real_escape_string($_POST['username']);
	$password = mysql_real_escape_string($_POST['password']); 

	$query = mysql_query("SELECT * FROM login_system WHERE username='$username' AND password='$password'");
	$query_rows = mysql_num_rows($query);

if($query_rows > 0) {
	echo "Successful Login!";

	session_start();
	$_SESSION['username'] = '$username';
	$_SESSION['password'] = '$password';
	echo "<meta http-equiv=\"refresh\" content=\"0;index.php\">";
} else {
	echo "Could not log in!";
	session_start();
	$_SESSION['login'] = '';
}

}

?>
<html>
<head><title>Login</title></head>
<body>
<form action="login.php" method="post" />
	Username: <input type="text" name="username"/>
	Password: <input type="password" name="password" />
		<input type="submit" value="Login" />
	</form>
</body>
</html>
[/code]

logout.php

[code]<?php

session_start();
session_destroy();

echo "<meta http-equiv=\"refresh\" content=\"0;index.php\">";


?>[/code]

register.php

[code]<?php

include "connect.php";

if($_SERVER['REQUEST_METHOD'] == 'POST') {
	$username = mysql_real_escape_string($_POST['username']);
	$password = mysql_real_escape_string($_POST['password']);
	$email = mysql_real_escape_string($_POST['email']);

	if(empty($username)) {
		echo "You must fill in a username!";
	} else {
	if(empty($password)) {
		echo "You must fill in a password!";
	} else {
		$query = mysql_query("SELECT * FROM login_system WHERE username='$username'");
		$rows = mysql_num_rows($query);
		if($rows > 0){
			echo "Username already taken!";
		} else {
			$insert_data = mysql_query("INSERT INTO login_system (id,username,password,email) VALUES ('','$username','$password','$email')");
				echo "Sucessfully Registered!";

					echo "<meta http-equiv=\"refresh\" content=\"0;index.php\">";
			}
		}
	}
}

?>

<html>
<head>
<title>Register</title></head>
<body>

<form action="register.php" method="post" />
	Username: <input type="text" name="username" /></br>
	Password: <input type="password" name="password" /></br>
	Email: <input type="text" name="email" /></br>
		<input type="submit" value="Register" />
</form>
</body>
</html>[/code]

